import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';

const LogIn = ({ mockLogIn }) => {
  const [userName, setUserName] = useState('');
  const [redirectToHome, setRedirectToHome] = useState(false); // Track if we need to redirect

  const handleSubmit = (e) => {
    e.preventDefault();
    if (userName) {
      mockLogIn({ userName });
      setRedirectToHome(true); // Set the redirect flag to true
    }
  };

  // If the redirect flag is set, redirect to the home page
  if (redirectToHome) {
    return <Redirect to="/" />;
  }

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
        placeholder="Enter your username"
      />
      <button type="submit">Log In</button>
    </form>
  );
};

export default LogIn;
