import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, useHistory } from 'react-router-dom';

// Import other components
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import LogIn from './components/Login';
import Credits from './components/Credits';
import Debits from './components/Debits';

const App = () => {
  const [accountBalance, setAccountBalance] = useState(1234567.89);
  const [creditList, setCreditList] = useState([]);
  const [debitList, setDebitList] = useState([]);
  const [currentUser, setCurrentUser] = useState({
    userName: 'Joe Smith',
    memberSince: '11/22/99',
  });

  const history = useHistory();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const creditsResponse = await fetch('https://moj-api.herokuapp.com/credits');
        const debitsResponse = await fetch('https://moj-api.herokuapp.com/debits');

        const creditList = await creditsResponse.json();
        const debitList = await debitsResponse.json();

        const creditTotal = creditList.reduce((sum, item) => sum + item.amount, 0);
        const debitTotal = debitList.reduce((sum, item) => sum + item.amount, 0);
        const accountBalance = creditTotal - debitTotal;

        setCreditList(creditList);
        setDebitList(debitList);
        setAccountBalance(accountBalance);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const addCredit = (credit) => {
    const updatedCredits = [...creditList, credit];
    const newBalance = accountBalance + credit.amount;
    setCreditList(updatedCredits);
    setAccountBalance(newBalance);
  };

  const addDebit = (debit) => {
    const updatedDebits = [...debitList, debit];
    const newBalance = accountBalance - debit.amount;
    setDebitList(updatedDebits);
    setAccountBalance(newBalance);
  };

  // Update state's currentUser (userName) after "Log In" button is clicked
  const mockLogIn = (logInInfo) => {
    const newUser = { ...currentUser };
    newUser.userName = logInInfo.userName;
    setCurrentUser(newUser);

    // Redirect to Home page after login
    history.push('/');
  };

  const HomeComponent = () => (<Home accountBalance={accountBalance} />);
  const UserProfileComponent = () => (
    <UserProfile userName={currentUser.userName} memberSince={currentUser.memberSince} />
  );
  const LogInComponent = () => (<LogIn user={currentUser} mockLogIn={mockLogIn} />);

  const CreditsComponent = () => (
    <Credits 
      credits={creditList} 
      addCredit={addCredit}
      accountBalance={accountBalance}
    />
  );

  const DebitsComponent = () => (
    <Debits 
      debits={debitList} 
      addDebit={addDebit}
      accountBalance={accountBalance}
    />
  );

  return (
    <Router basename="/bank-of-react-starter-code">
      <div>
        <Route exact path="/" render={HomeComponent} />
        <Route exact path="/userProfile" render={UserProfileComponent} />
        <Route exact path="/login" render={LogInComponent} />
        <Route exact path="/credits" render={CreditsComponent} />
        <Route exact path="/debits" render={DebitsComponent} />
      </div>
    </Router>
  );
};

export default App;
